var assert = require('assert'),
    errs = require('../lib/errs');

var macros = exports;

function assertTransparentStack(err) {
  assert.isString(err.stack);
  err.stack.split('\n').forEach(function (line) {
    assert.isFalse(/\/lib\/errs\.js\:/.test(line));
  });
}

